    private void bt_alumno_registrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_bt_alumno_registrarMouseClicked
        // TODO add your handling code here:
        String nombre, carrera, lugarN, numeroC, usuario, password;
        int edad, aprendizaje;

        nombre = tf_alumno_nombre.getText();
        carrera = tf_alumno_carrera.getText();
        lugarN = tf_alumno_nacimiento.getText();
        numeroC = tf_alumno_cuenta.getText();
        usuario = tf_alumno_usuario.getText();
        password = tf_alumno_contra.getText();
        edad = Integer.parseInt(tf_alumno_edad.getText());
        aprendizaje = Integer.parseInt(tf_alumno_aprendizaje.getText());

        normales.add(new Alumnos_normales(aprendizaje, nombre, edad, carrera, lugarN, numeroC, usuario, password));

        tf_alumno_nombre.setText("");
        tf_alumno_carrera.setText("");
        tf_alumno_contra.setText("");
        tf_alumno_aprendizaje.setText("");
        tf_alumno_edad.setText("");
        tf_alumno_cuenta.setText("");
        tf_alumno_nacimiento.setText("");
        tf_alumno_usuario.setText("");

        JOptionPane.showMessageDialog(this, "Se ha registrado correctamente");


    }//GEN-LAST:event_bt_alumno_registrarMouseClicked

